import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useForm } from 'react-hook-form'
import { useHistory } from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux'
import { loginUser } from '../redux/actions/authActions'
import { getCart } from '../redux/actions/cartActions'
import undraw_Add_user from '../assets/undraw_Add_user.svg'

const Login = () => {
  const { register, handleSubmit, errors } = useForm()
  const dispatch = useDispatch()
  const successfulLogin = useSelector((state) => state.isSuccess)
  const history = useHistory()
  console.log(errors)

  const onSubmit = (data) => {
    dispatch(loginUser(data, history))
    dispatch(getCart())
    let path = `/`
    history.push(path)
  }

  // useEffect(() => {
  //   console.log('getin cart!!!!')
  //   dispatch(getCart())
  // }, [successfulLogin])
  // useEffect(() => {
  //   if (successfulLogin) {
  //     dispatch(getCart());
  //   }
  // }, []);

  return (
    <div style={{ maxWidth: '800px', margin: 'auto', padding: '20px' }}>
      <h4>Login</h4>
      <form onSubmit={handleSubmit(onSubmit)}>
        <label>Username</label>
        <input type='text' id='username' name='username' ref={register} />
        <label>Password</label>
        <input type='password' id='password' name='password' ref={register} />
        <button class='btn waves-effect waves-light' type='submit'>
          {' '}
          Submit{' '}
        </button>
      </form>
      <div>
        <Link to='/register'>
          <p>New to GoSaveMore?</p>
          <button
            style={{ marginBottom: '20px' }}
            className='btn waves-effect waves-light'
          >
            Register
          </button>
        </Link>
      </div>
      <>
        <img src={undraw_Add_user} />
      </>
    </div>
  )
}

export default Login
