import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import CartCard from './CartCard'
import { getCart } from '../redux/actions/cartActions'
import './CartList.scss'
import { Button } from 'react-materialize'

const CartList = () => {
  const cartData = useSelector((state) => state.cart)
  const userID = useSelector((state) => state.auth.user.id)

  const [products, setProducts] = useState({
    totalItems: 0,
    totalPrice: 0,
  })

  const [cartDataA, setCartData] = useState()

  // console.log("HERE  :::::  ++++", cartData.savedItems.length)

  const dispatch = useDispatch()

  // useEffect(() => {
  //   setProducts({
  //     totalItems: cartData.totalItems,
  //     totalPrice: cartData.totalPrice,
  //   })
  // }, [dispatch])

  useEffect(() => {
    dispatch(getCart(userID))
    setCartData(...cartData)
    setProducts({
      totalItems: cartData.savedItems,
      totalPrice: cartData.totalPrice,
    })
    console.log('useEffect==>', cartDataA)
  }, [dispatch])

  if (cartDataA) {
    console.log('ifffff')

    return (
      <div className='cart-page'>
        <div className='cart-ring-up'>
          <h3> Total Items</h3>
          <p>{cartData.totalItems}</p>
          <h3> Total Price</h3>
          <p>${cartData.totalPrice}</p>
          <Button>Checkout</Button>
        </div>
        <div className='cart-list'>
          <div className='cart-list-product'>
            <h3>Your Shopping Cart</h3>
            {cartData.savedItems.map((product) => {
              if (product.quantity !== 0) {
                return (
                  <CartCard
                    key={product.id}
                    product={product}
                    setProducts={setProducts}
                    totalItems={products.totalItems}
                    totalPrice={products.totalPrice}
                  />
                )
              }
            })}
          </div>
        </div>
      </div>
    )
  } else {
    return (
      <div className='cart-page'>
        <div className='cart-ring-up'>
          <h3> Total Items</h3>
          <p>{products.totalItems}</p>
          <h3> Total Price</h3>
          <p>${products.totalPrice}</p>
          <Button>Checkout</Button>
        </div>
        <div className='cart-list'>
          <div className='cart-list-product'>
            <h3>Your Shopping Cart</h3>
            {cartData.items.map((product) => {
              if (product.quantity !== 0) {
                return (
                  <CartCard
                    key={product.id}
                    product={product}
                    setProducts={setProducts}
                    totalItems={products.totalItems}
                    totalPrice={products.totalPrice}
                  />
                )
              }
            })}
          </div>
        </div>
      </div>
    )
  }
}

export default CartList

