import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useSelector } from 'react-redux'
import CartCard from './CartCard'
import { Button } from 'react-materialize'
import './CartList.scss'

const CartList = () => {
  const { isLoading, items } = useSelector((state) => state.cart)

  const [total, setTotal] = useState(0)
  const [totalPrice, setTotalPrice] = useState(0)

  // if (!isLoading) {
  //   const addDecimals = (num) => {
  //     return (Math.round(num * 100) / 100).toFixed(2)
  //   }

  //   items.price = addDecimals(
  //     items.reduce(setTotalPrice((acc, item) => acc + item.price * item.quantity, 0))
  //   )
  // }

  useEffect(() => {
    if (!isLoading) {
      const addDecimals = (num) => {
        return (Math.round(num * 100) / 100).toFixed(2)
      }

      items.price = addDecimals(
        items.reduce(
          setTotalPrice((acc, item) => acc + item.price * item.quantity, 0)
        )
      )
    }
    setTotal(
      (items.quantity = items.reduce((acc, item) => acc + item.quantity, 0))
    )
  }, [])

  return (
    <div className='cart-page'>
      <div className='cart-ring-up'>
        <h3> Total Items</h3>
        <p>{total}</p>
        <h3> Total Price</h3>
        <p>${items.price}</p>
        <Link to={`/checkout/`} props={items.price} totalPrice={totalPrice}>
          <Button>Checkout</Button>
        </Link>
      </div>
      <div className='cart-list'>
        <div className='cart-list-product'>
          <h3>Your Shopping Cart</h3>
          {items.map((product) => {
            if (product.quantity !== 0) {
              return (
                <CartCard
                  key={product.id}
                  product={product}
                  total={total}
                  setTotal={setTotal}
                />
              )
            }
          })}
        </div>
      </div>
    </div>
  )
}

export default CartList

